import logo from './logo.svg'
import './App.css'
import axios from 'axios'
import React, {useState, useEffect} from 'react'
import { BrowserRouter, Route, Routes } from 'react-router-dom'

import { CustomNavbar } from './mainComponents/CustomNavbar'
import { CustomFooter } from './mainComponents/CustomFooter'
import { Container, Header, Content, Footer, Sidebar } from 'rsuite'

import { Theme, presetGpnDefault } from '@consta/uikit/Theme'
import { Card } from '@consta/uikit/Card'
import { Text } from '@consta/uikit/Text'
import { Button } from '@consta/uikit/Button'
import { Grid, GridItem } from '@consta/uikit/Grid'

import { PostsPage } from './components/PostsPage'
import { InfoPage } from './components/InfoPage'
import { HomePage } from './components/HomePage'


function App() {
    const [content, setContent] = useState([])
    const [posts, setPosts] = useState([])

    const [valueError, setValueError] = useState('')
    const [isClick, setIsClick] = useState(false)

    const clickOnPost = (event) => {
        console.log(event.target.id)
        let id = event.target.id
        window.location.assign('/infopage/' + id + '/')
        setIsClick(true)
    }

    useEffect(() => {
        axios({
            method: 'GET',
            url: window.location.origin+'/api/postsview/',
        }).then(response => {
            console.log(JSON.parse(response.data))
            //setContent(JSON.parse(response.data))
            setPosts(JSON.parse(response.data)['posts'])
            //doPosts(JSON.parse(response.data))
        }).catch(error => {
            console.log(error)
            setPostsValueError('Quasi per errorem inciderunt')
        })
    }, [])

    return (
        <Theme preset={presetGpnDefault}>
            <div className="show-container" >
            <BrowserRouter>
                <Container>
                    <Header>
                        <CustomNavbar/>
                    </Header>
                    <Content className='main-container' >
                        <div>
                            <Routes>
                                <Route path='/postinfo/' element={<PostsPage/>}/>
                                <Route path='/infopage/:id/' element={<InfoPage/>}/>
                            </Routes>
                        </div>
                    {
                (isClick === false) && (
                <div>
            {posts.map((post, index) => (
                <Grid col='12'>
                    <Card className='post-preview-card-style' horizontalSpace='xl' >
                        <Grid cols='12'>
                            <GridItem col='12'>
                            <Text view='link' cursor='pointer' id={post.id} onClick={(event) => clickOnPost(event)}>
                                {post.title}
                            </Text>
                            </GridItem>
                        </Grid>
                    </Card>
                </Grid>
            ))}
                </div>
                )
            }
                    </Content>
                    <CustomFooter />
                </Container>
            </BrowserRouter>
            </div>
        </Theme>
    )
}

export default App;
